import React, { Component, Fragment} from "react";
import Header from './components/Header/Header';
import Body from './components/Body/Body';
import './App.css';
import { connect, sendMsg } from "./api";
import AnimatedNumber from 'react-animated-number';
import prettyBytes from 'pretty-bytes';


import RaisedButton from 'material-ui/RaisedButton';
import TextField from 'material-ui/TextField';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';

class App extends Component {

  //state = {infoprocess:{},inforam:{},inforam:{},chatHistory:new Array()}
  style = {
    margin: 15,
   };
  state = {
    Cpuu: [["Time","Cpu"]],
    Infop: new Array(),
    Memfree : -1,
    Mempercent: [["Time","Ram"]],
    Memtotal : -1,
    Procsr : -1,
    Procss : -1,
    Procst : -1,
    Procsz : -1,
    Totalprocs : -1,
    autorize: false
  }
  constructor(props) {
    console.log("Constructor de App")
    super(props);
    //connect();

    console.log(this.state.Cpuu);
  }

  componentDidMount() {
    connect((msg) => {
      //console.log("New Message")
      let today = new Date();
      let date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate();
      let time = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
      let dateTime = date+' '+time;
      //console.log(this.state.Cpuu);
      //console.log(msg);
      let obj = JSON.parse(msg.data);
      //console.log(obj)
      this.setState(prevState => ({
        //chatHistory: [...this.state.chatHistory, msg]
        Cpuu:[...this.state.Cpuu,[dateTime,obj.Cpuu]],
        Infop: obj.Infop,
        Memfree: obj.Memfree,
        //[Date.now().toLocaleString(),obj.Mempercent]
        Mempercent: [...this.state.Mempercent,[dateTime,obj.Mempercent]] ,
        Memtotal: obj.Memtotal,
        Procsr: obj.Procsr,
        Procss: obj.Procss,
        Procst: obj.Procst,
        Procsz: obj.Procsz,
        Totalprocs : obj.Totalprocs

      }))

      console.log(obj);
    });
  }
  send(pid) {
    //console.log(pid)
    sendMsg(pid);
    //console.log(event.target.value)
    /*if(event.keyCode === 13) {
      sendMsg(event.target.value);
      event.target.value = "";
    }
    */
  }

  render() {

   if(this.state.autorize){

    return (
      <Fragment>
        <Header />
        <AnimatedNumber component="text" value={this.state.Memtotal}
              style={{
                  transition: '0.8s ease-out',
                  fontSize: 48,
                  transitionProperty:
                      'background-color, color, opacity'
              }}
              frameStyle={perc => (
                  perc === 100 ? {} : {backgroundColor: '#ffeb3b'}
              )}
              duration={300}
              formatValue={n => prettyBytes(n)}/>
        <Body data={this.state.Infop} send={this.send} datacpu={this.state.Cpuu} dataram={this.state.Mempercent}/>
      </Fragment>
    );



   }
   else{
    return (
      <Fragment>


        <MuiThemeProvider>
          <div>

           <TextField
             hintText="Enter your Username"
             floatingLabelText="Username"
             onChange = {(event,newValue) => this.setState({username:newValue})}
             />
           <br/>
             <TextField
               type="password"
               hintText="Enter your Password"
               floatingLabelText="Password"
               onChange = {(event,newValue) => this.setState({password:newValue})}
               />
             <br/>
             <RaisedButton label="Submit" primary={true} style={this.style} onClick={(event) => this.handleClick(event)}/>
         </div>
         </MuiThemeProvider>
         </Fragment>
    );

   }

  }
}

export default App;
