import React, { Component, Fragment} from "react";
import Header from './components/Header/Header';
import Body from './components/Body/Body';
import './App.css';
import { connect, sendMsg } from "./api";
import AnimatedNumber from 'react-animated-number';
import prettyBytes from 'pretty-bytes';


import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://material-ui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles(theme => ({
  '@global': {
    body: {
      backgroundColor: theme.palette.common.white,
    },
  },
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

class App extends Component {

  //state = {infoprocess:{},inforam:{},inforam:{},chatHistory:new Array()}
  state = {
    Cpuu: [["Time","Cpu"]],
    Infop: new Array(),
    Memfree : -1,
    Mempercent: [["Time","Ram"]],
    Memtotal : -1,
    Procsr : -1,
    Procss : -1,
    Procst : -1,
    Procsz : -1,
    Totalprocs : -1,
    autorize: false
  }
  constructor(props) {
    console.log("Constructor de App")
    super(props);
    //connect();

    console.log(this.state.Cpuu);
  }

  componentDidMount() {
    connect((msg) => {
      //console.log("New Message")
      let today = new Date();
      let date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate();
      let time = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
      let dateTime = date+' '+time;
      //console.log(this.state.Cpuu);
      //console.log(msg);
      let obj = JSON.parse(msg.data);
      //console.log(obj)
      this.setState(prevState => ({
        //chatHistory: [...this.state.chatHistory, msg]
        Cpuu:[...this.state.Cpuu,[dateTime,obj.Cpuu]],
        Infop: obj.Infop,
        Memfree: obj.Memfree,
        //[Date.now().toLocaleString(),obj.Mempercent]
        Mempercent: [...this.state.Mempercent,[dateTime,obj.Mempercent]] ,
        Memtotal: obj.Memtotal,
        Procsr: obj.Procsr,
        Procss: obj.Procss,
        Procst: obj.Procst,
        Procsz: obj.Procsz,
        Totalprocs : obj.Totalprocs

      }))

      console.log(obj);
    });
  }
  send(pid) {
    //console.log(pid)
    sendMsg(pid);
    //console.log(event.target.value)
    /*if(event.keyCode === 13) {
      sendMsg(event.target.value);
      event.target.value = "";
    }
    */
  }

  render() {
    const classes = useStyles();
   if(this.state.autorize){

    return (
      <Fragment>
        <Header />
        <AnimatedNumber component="text" value={this.state.Memtotal}
              style={{
                  transition: '0.8s ease-out',
                  fontSize: 48,
                  transitionProperty:
                      'background-color, color, opacity'
              }}
              frameStyle={perc => (
                  perc === 100 ? {} : {backgroundColor: '#ffeb3b'}
              )}
              duration={300}
              formatValue={n => prettyBytes(n)}/>
        <Body data={this.state.Infop} send={this.send} datacpu={this.state.Cpuu} dataram={this.state.Mempercent}/>
      </Fragment>
    );



   }
   else{
    return (
      <Fragment>
        <CssBaseline />
        <div className={classes.paper}>
          <Avatar className={classes.avatar}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Sign in
          </Typography>
          <form className={classes.form} noValidate>
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              autoFocus
            />
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
            />
            <FormControlLabel
              control={<Checkbox value="remember" color="primary" />}
              label="Remember me"
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
            >
              Sign In
            </Button>
            <Grid container>
              <Grid item xs>
                <Link href="#" variant="body2">
                  Forgot password?
                </Link>
              </Grid>
              <Grid item>
                <Link href="#" variant="body2">
                  {"Don't have an account? Sign Up"}
                </Link>
              </Grid>
            </Grid>
          </form>
        </div>
        <Box mt={8}>
          <Copyright />
        </Box>
      </Fragment>
    );

   }

  }
}

export default App;
