import React, { Component, Fragment} from "react";
import Header from './components/Header/Header';
import Body from './components/Body/Body';
import ChatHistory from './components/chatHistory/chatHistory'
import ChatInput from './components/ChatInput/ChatInput'
import logo from './logo.svg';
import './App.css';
import { connect, sendMsg } from "./api";
class App extends Component {
  state = {infoprocess:{},inforam:{},inforam:{},chatHistory:new Array()}
  constructor(props) {
    super(props);
    //connect();
  }
  componentDidMount() {
    connect((msg) => {
      console.log("New Message")
      //console.log(msg);
      let obj = JSON.parse(msg.data);
      console.log(obj)
      console.log(obj.type);
      switch(obj.type){
        case 1:{
          console.log("Es de tipo 1");
          console.log(obj.body);
          break;
        }
        case 2:{
          console.log("Es de tipo 2");
          console.log(obj.body);
          break;
        }
        case 3:{
          console.log("Es de tipo 3");
          console.log(obj.body);
          break;
        }
        case 4:{
          console.log("Es de tipo 4");
          console.log(obj.body);
          break;
        }
        default:{
          console.log("Entro en default: "+obj.type);
        }
      }


      this.setState(prevState => ({
        chatHistory: [...this.state.chatHistory, msg]
      }))
      console.log(this.state);
    });
  }
  send(event) {
    if(event.keyCode === 13) {
      sendMsg(event.target.value);
      event.target.value = "";
    }
  }

  render() {
    /*
    return (
      <div className="App">
      <Header />
      <ChatHistory chatHistory={this.state.chatHistory} />
      <ChatInput send={this.send} />
    </div>
    );
    */
   return (
    <Fragment>
      <Header />
      <Body errores={this.state.data}/>
    </Fragment>
  );
  }
}

export default App;
