import React, { Component, Fragment} from "react";
import Header from './components/Header/Header';
import Body from './components/Body/Body';
import Login from './components/Login/Login';
import './App.css';
import { connect, sendMsg } from "./api";
import AnimatedNumber from 'react-animated-number';
import prettyBytes from 'pretty-bytes';



import {TextField} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { Button } from "@material-ui/core";

class App extends Component {


  currencies = [
    {
      value: 'USD',
      label: '$',
    },
    {
      value: 'EUR',
      label: '€',
    },
    {
      value: 'BTC',
      label: '฿',
    },
    {
      value: 'JPY',
      label: '¥',
    },
  ];
  //state = {infoprocess:{},inforam:{},inforam:{},chatHistory:new Array()}

  state = {
    cpuinfo:-1,
    meminfo:-1,
    Cpuu: [["Time","Cpu"]],
    Infop: new Array(),
    Memfree : -1,
    Mempercent: [["Time","Ram"]],
    Memtotal : -1,
    Procsr : -1,
    Procss : -1,
    Procst : -1,
    Procsz : -1,
    Totalprocs : -1,
    autorize: false,
    username:"",
    pass:""
  }
  constructor(props) {
    console.log("Constructor de App")
    super(props);
    //connect();

    console.log(this.state.Cpuu);
  }
  handleUserChange = (event) =>{
    console.log("NOMBRE DEL USER:"+event.target.value);
    this.setState({username:event.target.value})
  }
  handlePassChange = (event) =>{
    console.log("NOMBRE DEL PASS:"+event.target.value);
    this.setState({pass:event.target.value})
  }
  loguearse = () =>{
    if(this.state.username=="admin" && this.state.pass=="admin"){
      this.setState({autorize:true});
    }
  }
  componentDidMount() {
    connect((msg) => {
      //console.log("New Message")
      let today = new Date();
      let date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate();
      let time = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
      let dateTime = date+' '+time;
      //console.log(this.state.Cpuu);
      //console.log(msg);
      let obj = JSON.parse(msg.data);
      //console.log(obj)
      this.setState(prevState => ({
        //chatHistory: [...this.state.chatHistory, msg]
        Cpuu:[...this.state.Cpuu,[dateTime,obj.Cpuu]],
        Infop: obj.Infop,
        Memfree: obj.Memfree,
        //[Date.now().toLocaleString(),obj.Mempercent]
        Mempercent: [...this.state.Mempercent,[dateTime,obj.Mempercent]] ,
        Memtotal: obj.Memtotal,
        Procsr: obj.Procsr,
        Procss: obj.Procss,
        Procst: obj.Procst,
        Procsz: obj.Procsz,
        Totalprocs : obj.Totalprocs,
        cpuinfo:obj.Cpuu,
        meminfo:obj.Mempercent

      }))

      console.log(obj);
    });
  }
  send(pid) {
    //console.log(pid)
    sendMsg(pid);
    //console.log(event.target.value)
    /*if(event.keyCode === 13) {
      sendMsg(event.target.value);
      event.target.value = "";
    }
    */
  }

  render() {


   if(this.state.autorize){

    return (
      <Fragment>
        <Header />

        <Body
         totalProcs={this.state.Totalprocs}
         procsS={this.state.Procss}
         procsR={this.state.Procsr}
         procsT={this.state.Procst}
         procsZ={this.state.Procsz}
         cpu = {this.state.cpuinfo}
         memTotal={this.state.Memtotal}
         memConsume={this.state.Memfree}
         memPercent={this.state.meminfo}s
         data={this.state.Infop}
         send={this.send}
         datacpu={this.state.Cpuu} dataram={this.state.Mempercent}/>
      </Fragment>
    );



   }
   else{
    return (
      <Fragment>
       <Login handleuser={this.handleUserChange} handlepass={this.handlePassChange} loguearse={this.loguearse}></Login>
      </Fragment>
    );

   }

  }
}

export default App;
