import React, { Component } from "react";
import Paper from "@material-ui/core/Paper";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
//import Editor from './Editor';
//import List from '@material-ui/core/List';
//import ListItem from '@material-ui/core/ListItem';
//import ListItemText from '@material-ui/core/ListItemText';
//import ListSubheader from '@material-ui/core/ListSubheader';
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
//import Paper from '@material-ui/core/Paper';
import Button from "@material-ui/core/Button";
import { makeStyles } from "@material-ui/core/styles";
import { Chart } from "react-google-charts";
import AnimatedNumber from 'react-animated-number';
import prettyBytes from 'pretty-bytes';
let id = 0;

/*
function createData(name, calories, fat, carbs, protein) {
  id += 1;
  return { id, name, calories, fat, carbs, protein };
}
*/
function TabContainer(props) {
    console.log("data TabContainer")
    //console.log(props.data);
  switch (props.flag) {
    case 0: {
      return (
        <Paper style={{ overflow: "auto" }}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell align="right">PID</TableCell>
                <TableCell align="right">USUARIO</TableCell>
                <TableCell align="right">ESTADO</TableCell>
                <TableCell align="right">RAM%</TableCell>
                <TableCell align="right">NOMBRE</TableCell>
                <TableCell align="right">Opcions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {props.data.map(row => (
                <TableRow key={row.Pid}>
                  <TableCell align="right">{row.Pid}</TableCell>
                  <TableCell align="right">{row.User}</TableCell>
                  <TableCell align="right">{row.Estado}</TableCell>
                  <TableCell align="right">{row.Memoria}</TableCell>
                  <TableCell align="right">{row.Nombre}</TableCell>
                  <TableCell align="right">
                    <Button
                      variant="contained"
                      color="secondary"
                      onClick={() => props.send(row.Pid)}
                    >
                      kill
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Paper>
      );
    }
    case 1: {
        //console.log(props.data);
      return (
        <Paper style={{ overflow: "auto" }}>
          <Chart
            width={"auto"}
            height={"auto"}
            chartType="AreaChart"
            loader={<div>Loading Chart</div>}
            data={props.data}
            options={{
              title: "CPU server",
              hAxis: { title: "Time", titleTextStyle: { color: "#333" } },
              vAxis: { minValue: 0 },
              // For the legend to fit, we make the chart area smaller
              hartArea: { width: "90%", height: "80%" }
              // lineWidth: 25
            }}
          />
        </Paper>
      );
    }
    case 2: {
        //console.log(props.data);
        let usedmem = props.memTotal - props.memFree;
        return (
            <Paper style={{ overflow: "auto" }}>
              <AnimatedNumber component="text" value={props.memTotal}
              style={{
                  transition: '0.8s ease-out',
                  fontSize: 48,
                  transitionProperty:
                      'background-color, color, opacity'
              }}
              frameStyle={perc => (
                  perc === 100 ? {} : {backgroundColor: '#ffeb3b'}
              )}
              duration={300}
              formatValue={n => 'Memoria total: '+prettyBytes(n)}/>

              <AnimatedNumber component="text" value={usedmem}
              style={{
                  transition: '0.8s ease-out',
                  fontSize: 48,
                  transitionProperty:
                      'background-color, color, opacity'
              }}
              frameStyle={perc => (
                  perc === 100 ? {} : {backgroundColor: '#ffeb3b'}
              )}
              duration={300}
              formatValue={n => 'Memoria Utilizada: '+prettyBytes(n)}/>

              <AnimatedNumber component="text" value={props.memPercent}
              style={{
                  transition: '0.8s ease-out',
                  fontSize: 48,
                  transitionProperty:
                      'background-color, color, opacity'
              }}
              frameStyle={perc => (
                  perc === 100 ? {} : {backgroundColor: '#ffeb3b'}
              )}
              duration={300}
              formatValue={n => 'Porcentaje: '+prettyBytes(n)}/>


          <Chart
            width={"auto"}
            height={"auto"}
            chartType="AreaChart"
            loader={<div>Loading Chart</div>}
            data={props.data}
            options={{
              title: "RAM server",
              hAxis: { title: "Time", titleTextStyle: { color: "#333" } },
              vAxis: { minValue: 0 },
              // For the legend to fit, we make the chart area smaller
              hartArea: { width: "90%", height: "80%" }
              // lineWidth: 25
            }}
          />
        </Paper>
          );
    }
  }

}
export default class Body extends Component {
  state = {
    value: 0,

  };

  constructor(props) {
    super(props);
    this.titulos = ["Principal", "CPU", "RAM"];
    console.log("Constructor del Footer");
    console.log(this.props.data);
  }
  handleChange = (event, value) => {
    this.setState({ value });
  };
  handlecodeChange = code => {
    console.log("desde Footer:: " + code);
  };
  render() {
    const { value } = this.state;

    return (
      <Paper>
        <Tabs
          value={value}
          onChange={this.handleChange}
          indicatorColor="primary"
          textColor="primary"
          variant="scrollable"
          scrollButtons="auto"
          centered
        >
          {<Tab label={this.titulos[0]} />}
          {<Tab label={this.titulos[1]} />}
          {<Tab label={this.titulos[2]} />}
          {<Tab label={this.titulos[3]} />}
        </Tabs>

        {value === 0 && (
          <TabContainer
            flag={0}
            data={this.props.data}
            send={this.props.send}
            totalProcs={this.props.totalProcs}
            procsS={this.props.procsS}
            procsR={this.props.procsR}
            procsT={this.props.procsT}
            procsZ={this.props.procsZ}
            cpu = {this.props.cpu}
          ></TabContainer>
        )}
        {value === 1 && (
          <TabContainer
            flag={1}
            data={this.props.datacpu}
            send={this.props.send}

          ></TabContainer>
        )}
        {value === 2 && (
          <TabContainer
            flag={2}
            data={this.props.dataram}
            send={this.props.send}
          ></TabContainer>
        )}
      </Paper>
    );
  }
}
